    setRMPRODiscountStore: function() {
                var discountStore = Ext.getStore('products.RMProDiscountStore');

                var billingController =  RMdatalink.app.getController('BillingDetailsController') ;


                discountStore.removeAll();
                discountStore.sync() ;

                discountStore.setData(billingController.config.pricingData[3].discount_policy) ;
                discountStore.sync() ;


    },

    updateRmproProducts: function(inputFld, record_key) {
        console.log(inputFld.attributes['data-id'].value) ;

        var _id = inputFld.attributes['data-id'].value ;
        var value = inputFld.value ;

        var rmProBillingStore = Ext.getStore('products.RMProStore') ;
        var recIndex = rmProBillingStore.findExact('_id',_id) ;

        if(recIndex == -1){
            return ;
        }

        var record = rmProBillingStore.getAt(recIndex) ;

        record.set(record_key,value) ;

         RMdatalink.app.getController('RMProController').onRmProSelectUnselect();
    },

    updateRmproDiscount: function(inputFld, record_key) {
        console.log(inputFld.attributes['data-id'].value) ;

        var _id = inputFld.attributes['data-id'].value ;
        var value = inputFld.value ;

        var rmProBillingStore = Ext.getStore('products.RMProDiscountStore') ;
        var recIndex = rmProBillingStore.findExact('id',_id) ;

        if(recIndex == -1){
            return ;
        }

        var record = rmProBillingStore.getAt(recIndex) ;

        record.set(record_key,value) ;

    },

    enableDisableFlds: function(status) {
        Ext.ComponentQuery.query('#productRMPROaddUpdateFrmPanel')[0].setDisabled(status) ;

        var selectFlds = document.getElementsByClassName('prodRmprolstflds');

        for( var i = 0 ; i< selectFlds.length ; i++ ){

            selectFlds[i].disabled = status  ;

        }


        var list = Ext.ComponentQuery.query('#productSetupRmproListPanel')[0].down('#mainList') ;

        list.setDisableSelection(status);


        var rmproForm = Ext.ComponentQuery.query('#productRMPROaddUpdateFrmPanel')[0] ;

        rmproForm.down('#saveBundleBtn').setDisabled(status);
        rmproForm.down('#deleteButtonBtn').setDisabled(status);
        rmproForm.down('#clearBundleBtn').setDisabled(status);

        rmproForm.down('#rmProAddModuleBtn').setDisabled(status);
        rmproForm.down('#rmProDeleteModuleBtn').setDisabled(status);
        rmproForm.down('#rmProNewResetBtn').setDisabled(status);

        rmproForm.down('#rmProDiscountSaveBtn').setDisabled(status);
        rmproForm.down('#rmProDiscountDeleteBtn').setDisabled(status);
        rmproForm.down('#rmProDiscountClearBtn').setDisabled(status);


        var rmproMainPanel  = Ext.ComponentQuery.query('#productRMproMainPanel')[0];

        rmproMainPanel.down('#headerEditTxtLbl').setHidden(status) ;

        rmproMainPanel.down('#rMProMainSetupEditBtn').setHidden(!status) ;
        rmproMainPanel.down('#rMProMainSetupCancelBtn').setHidden(status) ;
    },

