Ext.define('RMdatalink.store.CompanySpecificTechSupportStore', {
    extend: 'Ext.data.Store',

    requires: [
        'RMdatalink.model.TechSupportLogsModel'
    ],

    config: {
        pageNo: 1,
        cachedData: {
            
        },
        totalNoOfPages: '1',
        model: 'RMdatalink.model.TechSupportLogsModel',
        storeId: 'CompanySpecificTechSupportStore',
        listeners: [
            {
                fn: 'onStoreUpdaterecord',
                event: 'updaterecord'
            }
        ]
    },

    onStoreUpdaterecord: function(store, record, newIndex, oldIndex, modifiedFieldNames, modifiedValues, eOpts) {
        console.log("updating");

        if(RMdatalink.app.getController('TechSupportLog').config.isStoreUpdated) {

            var globalTechLogStore  = Ext.getStore('TechSupportLogsStore') ;
            var recordToUpdate =  RMdatalink.app.getController('TechSupportLog').config.techLogRecordToUpdate ;
            RMdatalink.util.DataLoader.onRetailerMasterStoreUpdate(globalTechLogStore,
                                                                   recordToUpdate
                                                                    ,newIndex,oldIndex,modifiedFieldNames
                                                                    ,modifiedValues,eOpts);



          RMdatalink.app.getController('TechSupportLog').config.isStoreUpdated = false ;

            var recIndex = globalTechLogStore.findExact("time_start",recordToUpdate.data.time_start) ;

            var rec = globalTechLogStore.getAt(recIndex);
            if(rec){

                rec.set(recordToUpdate.data) ;
            }


        }
    }

});